package assignment2;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	  char c='c';
      int i=50;
      short s=3;
      long l=1000;
      float f=5.0f;
      double d=10.0;
      System.out.println("The charcter is "+ c);
      System.out.println("The value of integer is "+ i);
      System.out.println("The value of short is "+ s);
      System.out.println("The value of long  is "+ l);
      System.out.println("The value of float is "+ f);
      System.out.println("The value of double is "+ d);
      System.out.println("The sum of integer, short and long  is "+ (i+s+l));
      System.out.println("The sum of float and double is "+ (f+d));
      
     
	}

}
